const DivSapatos = document.querySelector('#lista-sapatos');
Sapatos.forEach((s) => {
  const h2 = document.createElement('h2');
  h2.textContent = s.nome;
  DivSapatos.appendChild(h2);

  const img = document.createElement('img');
  img.src = s.img;
  DivSapatos.appendChild(img);

  const p = document.createElement('p');
  p.textContent = s.valor;
  DivSapatos.appendChild(p);

  const checkboxRoupa = document.createElement('input');
  checkboxRoupa.type = 'checkbox';
  DivSapatos.appendChild(checkboxRoupa);
})